           channel.queueDeclare(RabbitConstant.QUEUE_NETEASE, true, false, false, null);
           channel.queueBind(RabbitConstant.QUEUE_NETEASE, RabbitConstant.EXCHANGE_WEATHER, "");
           channel.basicQos(1);
