@Component
@RequiredArgsConstructor
public class MessageProducer {

    // ÊûÑÈÄ†ÂáΩÊï∞Ê≥®ÂÖ•
    private final RabbitTemplate rabbitTemplate;

    private static final Gson gson = new Gson();

    RabbitTemplate.ConfirmCallback confirmCallback = (correlationData, ack, cause) -> {
        System.out.println("Ê∂àÊÅØid:" + correlationData);
        System.out.println("ack:" + ack);
        if (ack) {
            System.out.println("Ê∂àÊÅØÂèëÈÄÅÁ°ÆËÆ§ÊàêÂäü");
        } else {
            System.out.println("Ê∂àÊÅØÂèëÈÄÅÁ°ÆËÆ§Â§±Ë¥•:" + cause);
        }
    };

    RabbitTemplate.ReturnsCallback returnCallback = returnedMessage -> {
        System.out.println("========ÂèëÈÄÅÂ§±Ë¥•ÂõûÊéâ========");
        System.out.println("ÈÄÄÂõûÁºñÁ†Å: " + returnedMessage.getReplyCode() + ", ÈÄÄÂõûÊèèËø∞: " + returnedMessage.getReplyText());
        System.out.println("‰∫§Êç¢Êú∫: " + returnedMessage.getExchange() + ", Ë∑ØÁî±ÈîÆÔºö" + returnedMessage.getRoutingKey());
        System.out.println("Ê∂àÊÅØ‰∏ª‰Ωì: " + new String(returnedMessage.getMessage().getBody()));
        System.out.println("===========================");
    };

    // Áîü‰∫ßËÄÖÂèëÈÄÅÊ∂àÊÅØ
    public void sendMessages(Employee employee) {

        // Ê∂àÊÅØÂèëÈÄÅÁ°ÆËÆ§ÔºåÁ°ÆËÆ§Ê∂àÊÅØÊòØÂê¶Âà∞ËææbrokerÊúçÂä°Âô®
        rabbitTemplate.setConfirmCallback(confirmCallback);

        // Ê∂àÊÅØÂèëÈÄÅÂ§±Ë¥•ËøîÂõûÂà∞ÈòüÂàó‰∏≠
        // ÂøÖÈ°ªÈÖçÁΩÆ spring.rabbitmq.template.mandatory=true ÊâçËÉΩ‰ΩøÁî®
        rabbitTemplate.setReturnsCallback(returnCallback);

        // Ê∂àÊÅØÁöÑÈôÑÂä†‰ø°ÊÅØÔºåÂç≥Ëá™ÂÆö‰πâid
        final CorrelationData cd = new CorrelationData(employee.getNumber() + "-" + System.currentTimeMillis());
        rabbitTemplate.convertAndSend("springboot-exchange", "hr.employee", gson.toJson(employee), cd);
    }
}
