@Component
public class MessageConsumer {

    private static final Gson gson = new Gson();

    /**
     * Ê∂àË¥πËÄÖÁõëÂê¨Ê∂àÊÅØÔºåÂπ∂Â§ÑÁêÜÊé•Êî∂Âà∞ÁöÑÊ∂àÊÅØ
     */
    // @RabbitHandlerÊ≥®Ëß£ÔºåÊ†áËØÜËØ•ÊñπÊ≥ïÊòØ RabbitMQ ÁöÑÊ∂àÊÅØÂ§ÑÁêÜÊñπÊ≥ï
    @RabbitHandler
    // @RabbitListenerÊ≥®Ëß£ÔºåÊ†áËØÜËØ•ÊñπÊ≥ïÊòØ RabbitMQ ÁöÑÊ∂àÊÅØÁõëÂê¨Âô®
    @RabbitListener(bindings = {
            // ÁªëÂÆöÂà∞ÊåáÂÆöÁöÑÈòüÂàóÔºå‰ªéÊåáÂÆöÁöÑ‰∫§Êç¢Êú∫Êé•Êî∂Ê∂àÊÅØÔºå‰ΩøÁî®ÊåáÂÆöÁöÑË∑ØÁî±ÈîÆËøõË°åÁªëÂÆö„ÄÇ
            @QueueBinding(
                    value = @Queue(value = "springboot-queue", declare = "true"),
                    exchange = @Exchange(value = "springboot-exchange", declare = "true", type = "topic"),
                    key = "#")
    })
    // ÂèØ‰ª•‰ΩøÁî®@PayloadÊ≥®Ëß£ÔºåÊ†áËØÜËØ•ÊñπÊ≥ïÁöÑÂèÇÊï∞ÊòØÊ∂àÊÅØ‰Ωì
    public void receiveMessages(@Payload String message, Channel channel,
                                @Headers Map<String, Object> headers) {
        System.out.println("===========================");
        Employee employee = gson.fromJson(message, Employee.class);
        System.out.println("Êé•Êî∂Âà∞Ê∂àÊÅØÔºöÂëòÂ∑•ÁºñÂè∑Ôºö" + employee.getNumber()
                + "ÔºåÂëòÂ∑•ÂßìÂêçÔºö" + employee.getName()
                + "ÔºåÂëòÂ∑•Âπ¥ÈæÑÔºö" + employee.getAge());
        try {
            // ÊâãÂä®ackÁ°ÆËÆ§
            channel.basicAck((Long) headers.get(AmqpHeaders.DELIVERY_TAG), false);
        } catch (IOException e) {
            throw new RuntimeException(e);
        }
        System.out.println("===========================");
    }

}
