                              select g.goods_no goodsno, g.goods_id as goodsid, g.barcode as barcode
                               from goods g
                                where (g.goods_id in :ids)
                                or (g.barcode in :ids)
                                order by g.goods_no
                  """).setParameter("ids", ids).getResultList();
              return resultList.stream()
                      .map(result -> Goods.of((Object[]) result))
                      .toList();
          }
      }
